INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503445, 'Flibanserin', 'Norgestimate', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin. The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism. Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19. Some oral contraceptives are considered weak inhibitors of these isoenzymes.', 'DDInter', 1767317477, 'Caution is advised when flibanserin is used with hormonal contraceptives. Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', 'Caution is advised when flibanserin is used with hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
