INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503567, 'Estramustine', 'Prednisolone', 'Moderate', 'Estrogens may enhance the systemic effects of both endogenous and exogenous corticosteroids. The proposed mechanism is an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in a decreased rate of corticosteroid metabolic clearance. The interaction has been reported with estrogens or estrogen-containing oral contraceptives (OCs) and hydrocortisone, prednisone, and prednisolone.', 'DDInter', 1767317477, 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever an estrogen is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Synergism', 'Patients treated concomitantly with an estrogen-containing drug may require lower dosages of corticosteroids or adrenocorticotropic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503568, 'Estramustine', 'Testolactone', 'Moderate', 'Theoretically, estrogen-containing drugs may negate the pharmacologic effects of aromatase inhibitors in the treatment of hormone-dependent breast cancer. Aromatase inhibitors induce a state of estrogen deprivation in postmenopausal women by inhibiting the conversion of androgens to estrogens in adrenal and ovarian tissues, thus estrogen administration would be expected to reverse their therapeutic effects.', 'DDInter', 1767317477, 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', 'Antagonism', 'Use of estrogen-containing products should be avoided during aromatase inhibitor therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
