INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503596, 'Conjugated estrogens (topical)', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. Lonafarnib is a potent CYP450 3A time-dependent and mechanism-based inhibitor in vitro.', 'DDInter', 1767317477, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. The prescribing information recommends avoiding concomitant use of lonafarnib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges when possible. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503597, 'Conjugated estrogens (topical)', 'Metyrapone', 'Moderate', 'Estrogen therapy may reduce the response to metyrapone test. The mechanism is related to an increase in serum cortisol-binding globulin (transcortin) induced by estrogens, resulting in decreased cortisol clearance. The increase in circulating cortisol may directly interfere with test results, since the pharmacologic effect of metyrapone is to inhibit adrenal production of cortisol and corticosterone in order to elicit an increase in ACTH production by the pituitary.', 'DDInter', 1767317477, 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', 'Others', 'Clinicians should recognize the potential for a diminished response to metyrapone test in patients treated with estrogens.', NULL, NULL, 'Moderate', 'pharmacodynamic');
