INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503760, 'Eszopiclone', 'Troglitazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of zopiclone, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to zopiclone and eszopiclone should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hypnotic dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503761, 'Eszopiclone', 'Troleandomycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of both zopiclone and its pharmacologically active S(-) enantiomer, eszopiclone. Zopiclone has been shown in vitro to be metabolized by CYP450 3A4 and CYP450 2C8, while eszopiclone is primarily metabolized by CYP450 3A4 and 2E1 via demethylation and oxidation.', 'DDInter', 1767317477, 'Due to increased risk of next-day psychomotor impairment, the dosages of eszopiclone and zopiclone should be reduced when used with potent CYP450 3A4 inhibitors. The manufacturers recommend that total dose of eszopiclone not exceed 2 mg. An initial dose of 3.75 mg is recommended for zopiclone, which may be increased up to 5 mg if clinically indicated. Patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them, preferably at least 12 hours after administration of the hypnotic.', 'Metabolism', 'Due to increased risk of next-day psychomotor impairment, the dosages of eszopiclone and zopiclone should be reduced when used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
