INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503894, 'Etacrynic acid', 'Chlorpromazine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317477, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1503895, 'Etacrynic acid', 'Cisapride', 'Major', 'Some diuretics may produce hypokalemia and hypomagnesemia which could potentiate the risk of cisapride-induced arrhythmias in patients receiving both drugs concomitantly. Prolonged QT interval, torsades de pointes, and death have been reported.', 'DDInter', 1767317477, 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', 'Synergism', 'Cisapride is contraindicated in patients with uncorrected hypokalemia and hypomagnesemia, such as those taking potassium-wasting diuretics.', NULL, NULL, 'Major', 'pharmacodynamic');
