    ) VALUES (1503995, 'Etacrynic acid', 'Tubocurarine', 'Moderate', 'Loop diuretics may enhance or decrease the effect of nondepolarizing neuromuscular blockers. The mechanism is unknown.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored. The dose of the neuromuscular blocker may require adjustment to achieve the desired effect.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
