    ) VALUES (1504411, 'Frovatriptan', 'Etonogestrel', 'Minor', 'The concurrent use of oral contraceptives may increase the plasma concentrations of frovatriptan. The mechanism of interaction has not been established, and it is not known if hormonal contraceptives delivered by other routes (intravaginal, transdermal) may similarly affect the pharmacokinetics of frovatriptan. Regardless, this interaction is unlikely to be of clinical significance.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
