        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1505048, 'Iron', 'Calcium carbonate', 'Moderate', 'INTERVAL: The bioavailability of orally administered iron may be reduced by concomitant administration of antacids or other agents with acid-neutralizing effects. The exact mechanism is unknown but may involve reduced iron solubility due to increase in gastric pH and/or reduced absorption due to complexation or precipitation of the iron.', NULL, 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', NULL, 'To minimize the potential for interaction, it may be appropriate to administer oral iron preparations at least two hours apart from antacids or other agents with acid-neutralizing effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
