    ) VALUES (1505077, 'Calcium carbonate', 'Riociguat', 'Moderate', 'INTERVAL: Concurrent administration of riociguat with antacids may reduce its gastrointestinal absorption. Riociguat exhibits pH-dependent solubility, with increased solubility at lower pH.', NULL, 'Antacids should not be taken within 1 hour of riociguat administration.', NULL, 'Antacids should not be taken within 1 hour of riociguat administration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
