        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1505114, 'Aminophylline', 'Febuxostat', 'Moderate', 'Coadministration with febuxostat may alter the pharmacokinetics of theophylline. The proposed mechanism is inhibition of xanthine oxidase by febuxostat.', NULL, 'No dosage adjustment is necessary for theophylline when coadministered with febuxostat, but caution is advised.', NULL, 'No dosage adjustment is necessary for theophylline when coadministered with febuxostat, but caution is advised. Because the safety of long-term exposure to 1-methylxanthine is unknown, this should be taken into consideration when deciding to use the combination.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
