INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505203, 'Fedratinib', 'St. John''s Wort', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of fedratinib, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', 'Metabolism', 'According to the product labeling, concomitant use of fedratinib with potent or moderate CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505204, 'Fedratinib', 'Telaprevir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of fedratinib, which is a substrate of the isoenzyme. The risk of adverse reactions such as anemia, thrombocytopenia, diarrhea, nausea, vomiting, and liver (ALT, AST) and pancreatic (amylase, lipase) enzyme elevations may increase.', 'DDInter', 1767317477, 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided. However, if coadministration with a potent CYP450 3A4 inhibitor is necessary, the manufacturer recommends that the dose of fedratinib be reduced to 200 mg once daily. Patients should be advised to notify their health care professional if signs and symptoms of anemia, thrombocytopenia, or gastrointestinal, hepatic, or pancreatic toxicity develop. Further dosage adjustment may be required based on patient tolerance and response. When/if concomitant therapy with a potent CYP450 3A4 inhibitor is ceased, the dose of fedratinib should first be increased to 300 mg once daily for two weeks an then increased to 400 mg once daily thereafter as tolerated.', 'Metabolism', 'Coadministration of fedratinib with potent CYP450 3A4 inhibitors should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
