INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505217, 'Amprenavir', 'Felbamate', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505218, 'Anisindione', 'Felbamate', 'Moderate', 'Felbamate has been reported to increase the anticoagulant effect of warfarin. The mechanism may be inhibition of warfarin metabolism via the CYP450 system in the liver. However, causal effect has not been definitively established. Other oral anticoagulants could interact with felbamate in a similar manner.', 'DDInter', 1767317477, 'Close monitoring of the prothrombin time or the INR is recommended if felbamate and an oral anticoagulant must be used together. The anticoagulant dose may need to be decreased or temporarily stopped. Patients should be advised to notify their physician if they experience any signs of excessive anticoagulation, such as unusual or prolonged bleeding, bruising, vomiting, change in stool or urine color, headache, dizziness, or weakness.', 'Metabolism', 'Close monitoring of the prothrombin time or the INR is recommended if felbamate and an oral anticoagulant must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
