INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505260, 'Hydroxychloroquine', 'Felbamate', 'Moderate', 'Chloroquine or hydroxychloroquine may lower the convulsive threshold, which may antagonize the action of antiepileptic medications. Seizures have been rarely reported in patients on chloroquine or hydroxychloroquine therapy. The mechanism is unknown.', 'DDInter', 1767317477, 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy. In addition, patients with a history of epilepsy should be advised about the risk of chloroquine/hydroxychloroquine-induced seizures. Alternative anti-malarial agents may be required. Local treatment guidelines should be consulted.', 'Synergism', 'The potential risks and benefits should be evaluated prior to initiating chloroquine or hydroxychloroquine in patients on anticonvulsant therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
