INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505270, 'Felbamate', 'Levomefolic acid', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 1767317477, 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
