INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505288, 'Felbamate', 'Phenobarbital', 'Moderate', 'Felbamate may decrease the hepatic metabolism of phenobarbital. Phenobarbital toxicity may result. In addition, phenobarbital may induce felbamate metabolism resulting in decreased felbamate concentrations. It may take up to 1 month to reach steady state phenobarbital concentrations.', 'DDInter', 1767317477, 'If this combination cannot be avoided, the phenobarbital dose may require a 25% reduction. Patients should be closely monitored for clinical response and phenobarbital toxicity (drowsiness, ataxia, lethargy) for at least one month after initiating concomitant therapy. Decreased phenobarbital doses may be required if felbamate is discontinued.', 'Metabolism', 'If this combination cannot be avoided, the phenobarbital dose may require a 25% reduction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505289, 'Felbamate', 'Progesterone (topical)', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
