INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505297, 'Felbamate', 'St. John''s Wort', 'Moderate', 'Coadministration with potent CYP450 3A4 inducers may decrease the plasma concentrations of felbamate, which may be a substrate of the isoenzyme. Pharmacokinetic data may not be available for all potent inducers with respect to their effect on felbamate. However, the potent CYP450 3A4 inducers carbamazepine, phenobarbital, and phenytoin have all been shown to decrease felbamate plasma concentrations.', 'DDInter', 1767317477, 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy with felbamate.', 'Metabolism', 'Caution is advised if felbamate is used concomitantly with potent CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
