INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505407, 'Felodipine', 'St. John''s Wort', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505408, 'Felodipine', 'Tacrine', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 1767317477, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505409, 'Felodipine', 'Telaprevir', 'Moderate', 'Coadministration with the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317477, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with boceprevir or telaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
