INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1505482, 'Fenfluramine', 'Sibutramine', 'Major', 'Sibutramine is a potent inhibitor of norepinephrine and serotonin reuptake, and a less potent inhibitor of dopamine reuptake. As such, it may potentiate the pharmacologic response to sympathomimetic agents due to additive or synergistic effects. Although the interaction has not been studied with sibutramine, it has been reported with the selective serotonin reuptake inhibitor, fluoxetine.', 'DDInter', 1767317477, 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', 'Synergism', 'The use of sibutramine in combination with amphetamines and other centrally-acting appetite suppressants is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
