    ) VALUES (1505632, 'Fenoprofen', 'Phenobarbital', 'Minor', 'Barbiturates may increase the hepatic metabolism of fenoprofen. Serum levels and effects of fenoprofen may therefore be reduced.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
