        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1505652, 'Fenoprofen', 'Tositumomab', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
