    ) VALUES (1505778, 'Fesoterodine', 'Vibegron', 'Moderate', 'Concomitant use of vibegron with antimuscarinic agents used in the treatment of overactive bladder (OAB) may increase the risk of urinary retention.', NULL, 'Caution is advised if vibegron is prescribed in combination with antimuscarinic medications used for the treatment of OAB.', NULL, 'Caution is advised if vibegron is prescribed in combination with antimuscarinic medications used for the treatment of OAB. Vibegron should be discontinued in patients who develop urinary retention.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
