        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1505790, 'Histamine', 'Fexofenadine', 'Moderate', 'Antihistamines may suppress the patient''s response to histamine phosphate. The length of suppression varies and is dependent on the patient, type of antihistamine, and the duration of antihistamine therapy. The duration of suppression may range from 24 hours with chlorpheniramine or up to several weeks with second-generation antihistamines.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', NULL, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering histamine phosphate to patients treated with antihistamines.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
