    ) VALUES (1505794, 'Fexofenadine', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
