    ) VALUES (1505799, 'Fexofenadine', 'Trichophyton mentagrophytes', 'Moderate', 'INTERVAL: Antihistamines may diminish the response to diagnostic skin test antigens. Falsely insignificant or false-negative results may occur in such patients.', NULL, 'Antihistamines should be discontinued approximately 2 to 4 days prior to skin testing procedures.', NULL, 'Antihistamines should be discontinued approximately 2 to 4 days prior to skin testing procedures.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
