        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1505800, 'Fexofenadine', 'Troleandomycin', 'Minor', 'Coadministration with erythromycin has been shown to increase the oral bioavailability of fexofenadine. The proposed mechanism is erythromycin inhibition of the intestinal efflux of fexofenadine via P-glycoprotein transporter. In addition, erythromycin may decrease biliary excretion of fexofenadine. Fexofenadine had no effect on the pharmacokinetics of erythromycin.', NULL, '-', NULL, '-', 'Absorption, Excretion', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
