INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506467, 'Fluconazole', 'Doxorubicin (liposomal)', 'Moderate', 'Coadministration with fluconazole may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4-mediated metabolism by fluconazole, a moderate inhibitor of the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluconazole is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when fluconazole is used with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506468, 'Fluconazole', 'Ergometrine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 including azole antifungal agents may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors. Clinical ergotism has been reported, which can lead to gangrene and myocardial infarction in severe cases. Even small, single doses of ergotamine have resulted in clinically significant interactions, occasionally resulting in surgical amputation or death.', 'DDInter', 1767317477, 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible. Otherwise, close monitoring for adverse effects such as peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension is recommended. Patients should be advised to seek medical attention if they experience potential signs and symptoms of ergotism such as paresthesia, tingling of fingers, cold or painful extremities, numbness, muscle cramps, abdominal pain, rapid pulse, or chest pain.', 'Metabolism', 'Concomitant use of ergot derivatives with fluconazole at high, multiple doses or clotrimazole and miconazole mucous membrane preparations should generally be avoided, if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
