INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506525, 'Fluconazole', 'Norgestrel', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317477, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506526, 'Fluconazole', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of paclitaxel, which has been found in vitro to be a substrate of these isoenzymes.', 'DDInter', 1767317477, 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', 'Metabolism', 'Clinicians should consider the potential for interaction with drugs that inhibit CYP450 2C8 and/or 3A4 and monitor for evidence of dose-related toxicities of paclitaxel during coadministration, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506527, 'Fluconazole', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 1767317477, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506528, 'Fluconazole', 'Perphenazine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506529, 'Fluconazole', 'Phenobarbital', 'Minor', 'Coadministration with inhibitors of CYP450 2C19 may theoretically increase the plasma concentrations of phenobarbital, which is a substrate of the isoenzyme. No precautions appear to be necessary during coadministration of phenobarbital with CYP450 2C19 inhibitors. However, dosage adjustments may be necessary if an interaction is suspected.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506530, 'Fluconazole', 'Phenolphthalein', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317477, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506531, 'Fluconazole', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
