INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506589, 'Fludarabine', 'Cytarabine', 'Moderate', 'INTERVAL: Cytarabine can impair the metabolism of fludarabine. The mechanism is inhibition of phosphorylation of arabinosyl-2-fluoroadenine. As a result, plasma levels of the active fludarabine triphosphate, F-ara-ATP, are decreased. Also, F-ara-ATP can potentiate deoxycytidine kinase--that is, the phosphorylation of cytarabine. As a result, plasma levels of the active cytarabine triphosphate, ara-CTP, are increased.', 'DDInter', 1767317477, 'It has been suggested that cytarabine be given as a relatively short-term infusion (two to four hours) following fludarabine to minimize metabolic interference with the subsequent fludarabine dose and to maximize ara-CTP synthesis.', 'Metabolism', 'It has been suggested that cytarabine be given as a relatively short-term infusion (two to four hours) following fludarabine to minimize metabolic interference with the subsequent fludarabine dose and to maximize ara-CTP synthesis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
