INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506854, 'Fosphenytoin', 'Fluorouracil', 'Moderate', 'Concomitant administration with levamisole and 5-fluorouracil (5-FU) has been reported to increase serum levels of phenytoin. No mechanism has been established for this interaction, and it is unknown whether levamisole or 5-FU, or both, is responsible.', 'DDInter', 1767317477, 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary. Patients should be advised to notify their physician if they experience symptoms of phenytoin toxicity, including drowsiness, visual disturbances, mental status changes, seizures, nausea, or ataxia. It is uncertain whether this interaction may also occur with other hydantoins.', 'Others', 'Pharmacologic response to phenytoin should be monitored more closely whenever levamisole, 5-FU, or capecitabine (which is metabolized to 5-FU in vivo) is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506855, 'Gallium chloride Ga-67', 'Fluorouracil', 'Moderate', 'The concomitant use of certain medications (e.g., cytarabine, fluorouracil, methotrexate, and iron) may cause displacement of gallium citrate Ga-67 from plasma protein binding sites and interfere with test results.', 'DDInter', 1767317477, 'Clinicians should be aware of possible interference with diagnostic tests in patients who are taking these medications.', 'Distribution', 'Clinicians should be aware of possible interference with diagnostic tests in patients who are taking these medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1506856, 'Ganciclovir', 'Fluorouracil', 'Moderate', 'The use of ganciclovir with other potentially myelotoxic and/or nephrotoxic agents may increase the risk and severity of hematologic toxicity and/or nephrotoxicity due to both additive pharmacodynamic effects and additive effects on the kidney. Ganciclovir alone may cause severe leucopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow depression, aplastic anemia, increased serum creatinine levels, and acute renal failure, and the risk may theoretically increase when coadministered with other myelotoxic or nephrotoxic therapy.', 'DDInter', 1767317477, 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks. Extreme caution is advised if they are used in patients who have recently received or are receiving treatment with myelotoxic and/or nephrotoxic drugs, and vice versa. If coadministration is required, reduced dosages of one or more of the drugs may be required, and the patient should be monitored for the development of hematologic and/or renal adverse effects both during and after discontinuation of therapy.', 'Synergism', 'Ganciclovir or its prodrug, valganciclovir, should generally not be combined with other drugs associated with myelosuppression and/or nephrotoxicity unless the benefits are anticipated to outweigh the potential risks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
