INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507066, 'Fluphenazine', 'Mazindol', 'Moderate', 'Phenothiazines may antagonize the pharmacologic effects of amphetamine, amphetamine derivatives, and other centrally-acting sympathomimetic agents (i.e., CNS stimulants). Conversely, these agents may diminish the neuroleptic efficacy of phenothiazines. The exact mechanism of interaction is unknown but may involve opposing effects on dopaminergic activity.', 'DDInter', 1767317477, 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', 'Antagonism', 'Amphetamine, amphetamine derivatives, and other CNS stimulants should generally not be used, particularly for weight reduction, in patients treated with phenothiazines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507067, 'Fluphenazine', 'Mephenytoin', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
