INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507193, 'Flurbiprofen', 'Methoxyflurane', 'Moderate', 'Coadministration of methoxyflurane with other nephrotoxic agents may increase the risk of renal impairment. The precise mechanism has not been delineated.', 'DDInter', 1767317477, 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
