INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507240, 'Amoxapine', 'Flutamide', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507241, 'Anisindione', 'Flutamide', 'Moderate', 'Flutamide may enhance the hypoprothrombinemic response to warfarin by an unknown mechanism. In patients stabilized on warfarin, elevations in prothrombin time have been reported with the addition of flutamide to the drug regimen.', 'DDInter', 1767317477, 'Patients should be monitored for altered anticoagulation and advised to report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, bruising, red or brown urine, or red or black stools.', 'Synergism', 'Patients should be monitored for altered anticoagulation and advised to report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, bruising, red or brown urine, or red or black stools.', NULL, NULL, 'Moderate', 'pharmacodynamic');
