INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507668, 'Fosphenytoin', 'Fosaprepitant', 'Moderate', 'Theoretically, coadministration of phenytoin and aprepitant may result in decreased plasma concentrations of both drugs. The mechanism is phenytoin induction of CYP450 3A4, of which aprepitant is a substrate, and aprepitant induction of CYP450 2C9, of which phenytoin is a substrate.', 'DDInter', 1767317477, 'During coadministration of aprepitant and phenytoin, the possibility of a diminished therapeutic response to both drugs should be considered. Pharmacologic response should be monitored more closely whenever one or the other drug is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'During coadministration of aprepitant and phenytoin, the possibility of a diminished therapeutic response to both drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507669, 'Fostamatinib', 'Fosaprepitant', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 1767317477, 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', NULL, NULL, 'Moderate', 'pharmacodynamic');
