INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507710, 'Fosaprepitant', 'Terfenadine', 'Major', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of astemizole and terfenadine. The mechanism is inhibition of CYP450 3A4-mediated metabolism by aprepitant. High plasma levels of astemizole and terfenadine (e.g., due to overdose or interaction with other potent 3A4 inhibitors) have been associated with prolongation of the QT interval, which has led to cases of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', 'DDInter', 1767317477, 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, concomitant use of aprepitant or fosaprepitant is considered contraindicated. Loratadine, cetirizine, or fexofenadine may be safer alternatives.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of astemizole and terfenadine, concomitant use of aprepitant or fosaprepitant is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
