INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507822, 'Fosinopril', 'Sodium aurothiomalate', 'Moderate', 'Concomitant ACE inhibitor therapy may potentiate the risk of nitritoid (vasomotor) reactions associated with injectable gold therapy. The mechanism of interaction is unknown, although investigators suggest that ACE inhibitors may unmask drug hypersensitivity reactions by preventing the breakdown of bradykinins.', 'DDInter', 1767317477, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection. Elderly patients in particular should be observed following treatment. Oxygen, intravenous fluids, epinephrine, steroids, and other supportive therapy may be necessary for treatment.', 'Others', 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507823, 'Guanfacine', 'Fosinopril', 'Moderate', 'Use of guanfacine in combination with antihypertensive agents may result in additive pharmacodynamic effects such as hypotension and syncope.', 'DDInter', 1767317477, 'Caution is advised during coadministration of these agents. Dosage adjustments may be required and hemodynamic responses should be monitored during concomitant therapy. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'Caution is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
