INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507843, 'Interferon alfa-2a, Recombinant', 'Fosinopril', 'Moderate', 'The concomitant use of recombinant interferon-alpha-2a and some angiotensin converting enzyme (ACE) inhibitors has been associated with a higher incidence of granulocytopenia than either drug used alone in patients with cryoglobulinemia. The mechanism is unknown but may be related to an autoimmune response. It is also unknown whether patients with other conditions may be affected. Data exist for enalapril and captopril.', 'DDInter', 1767317477, 'Until more information is available, close hematologic monitoring is recommended.', 'Synergism', 'Until more information is available, close hematologic monitoring is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507844, 'Fosinopril', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 1767317477, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507845, 'Fosinopril', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', 'DDInter', 1767317477, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507846, 'Fosinopril', 'Magaldrate', 'Minor', 'Coadministration with antacids may decrease the oral bioavailability of captopril and other angiotensin converting enzyme (ACE) inhibitors due to delayed gastric emptying and/or elevated gastric pH. Based on available data, the clinical significance of this interaction appears to be minor. As a precaution, patients may want to consider separating the administration times of ACE inhibitors and antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) by 1 to 2 hours.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
