INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507855, 'Fosinopril', 'Nitroglycerin', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', 'DDInter', 1767317477, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1507856, 'Fosinopril', 'Opium', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 1767317477, 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
