    ) VALUES (1507988, 'Fosphenytoin', 'Quinidine', 'Moderate', 'Hydantoins may increase the CYP450 hepatic metabolism of quinidine. Serum quinidine levels and quinidine effectiveness may be decreased.', NULL, 'Quinidine levels and effectiveness should be monitored frequently after a hydantoin is added to or deleted from the patient''s regimen.', NULL, 'Quinidine levels and effectiveness should be monitored frequently after a hydantoin is added to or deleted from the patient''s regimen. An increase in quinidine dosage may be required. Patients should be advised to notify their physician if they experience increased irregular heartbeats or worsening of their symptoms.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
