        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1508211, 'Ginseng', 'Furosemide', 'Moderate', 'Ginseng has been reported to induce diuretic resistance in a patient treated with furosemide and cyclosporine.', NULL, 'Until further data are available, patients treated with furosemide or other loop diuretics should preferably avoid the use of ginseng supplements.', NULL, 'Until further data are available, patients treated with furosemide or other loop diuretics should preferably avoid the use of ginseng supplements. The possibility of diuretic resistance should be considered and appropriately monitored if concomitant use is necessary.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
