        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1508268, 'Furosemide', 'Rapacuronium', 'Moderate', 'Loop diuretics may enhance or decrease the effect of nondepolarizing neuromuscular blockers. The mechanism is unknown.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored.', NULL, 'The patient''s respiratory status and level of neuromuscular blockade should be closely monitored. The dose of the neuromuscular blocker may require adjustment to achieve the desired effect.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
