INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508751, 'Gemifloxacin', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', 'DDInter', 1767317477, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508752, 'Gemifloxacin', 'Levacetylmethadol', 'Major', 'Levomethadyl acetate may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. High dosages of levomethadyl acetate alone have been associated with QT interval prolongation and torsade de pointes.', 'DDInter', 1767317477, 'Coadministration of levomethadyl acetate with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of levomethadyl acetate with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508753, 'Gemifloxacin', 'Lindane', 'Moderate', 'Lindane penetrates human skin and has the potential to cause central nervous system toxicity. Seizures have been reported after excessive use or oral ingestion of lindane. There may be a theoretical risk of increased seizure potential when lindane is used with selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), and/or any substance that can reduce the seizure threshold (e.g., carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, theophylline).', 'DDInter', 1767317477, 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.g., head trauma, brain tumor, metabolic disorders, alcohol and drug withdrawal, CNS infections). Lindane should be used according to recommended dosage and directions for application.', 'Synergism', 'Caution is advised if lindane is used with any substance that can reduce the seizure threshold, particularly in the very young or the elderly and in patients with epilepsy, a history of seizures, or other risk factors for seizures (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
