INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508841, 'Diethylstilbestrol', 'Somatotropin', 'Moderate', 'Coadministration of oral estrogen replacement therapy may diminish the therapeutic effects of human growth hormone. Some of the pharmacodynamic effects of human growth hormone are primarily mediated by insulin-like growth factor 1 (IGF-1), and orally administered estrogen can suppress growth hormone (GH)-dependent IGF-1 production in the liver. Gender differences have been shown in responsiveness to GH, with women requiring higher doses than men to achieve a similar response.', 'DDInter', 1767317477, 'Patients receiving human growth hormone with oral estrogen replacement therapy should be monitored for potentially diminished GH efficacy. A higher dosage of growth hormone may be required to maintain serum IGF-1 levels within the normal age-appropriate range in order to achieve the defined treatment goal. Please consult individual product labeling for the appropriate initial dosage of growth hormone recommended. Dosage adjustments as well as clinical and laboratory monitoring should be considered following the addition or discontinuation of oral estrogen replacement therapy during use of growth hormone.', 'Antagonism', 'Patients receiving human growth hormone with oral estrogen replacement therapy should be monitored for potentially diminished GH efficacy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508842, 'Doxorubicin (liposomal)', 'Somatotropin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of doxorubicin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. Close monitoring for potentially reduced efficacy of doxorubicin is recommended if coadministration is required.', 'Metabolism', 'Concomitant use of doxorubicin with CYP450 3A4 inducers should generally be avoided, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
