INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508873, 'Somatotropin', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM. High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317477, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The dose or dosing interval may require adjustment if an interaction is suspected. A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508874, 'Somatotropin', 'Lomitapide', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508875, 'Somatotropin', 'Lonafarnib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of lonafarnib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of lonafarnib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
