INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508925, 'Gilteritinib', 'Colchicine', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 1767317477, 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
