INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1508953, 'Idelalisib', 'Gilteritinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of gilteritinib, which is primarily metabolized by the isoenzyme. Increased exposure to gilteritinib may increase the risk of serious adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections, and stomatitis.', 'DDInter', 1767317477, 'Concomitant use of gilteritinib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. Otherwise, close monitoring for increased adverse effects is advisable, including more frequent ECG monitoring. Gilteritinib treatment should be interrupted or dosage reduced in patients with serious or life-threatening toxicity in accordance with the product labeling.', 'Metabolism', 'Concomitant use of gilteritinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
