INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509125, 'Glasdegib', 'Troglitazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of glasdegib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of glasdegib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509126, 'Glasdegib', 'Troleandomycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme. Increased exposure to glasdegib may increase the risk of serious adverse effects such as QT interval prolongation, anemia, febrile neutropenia, thrombocytopenia, hemorrhage, edema, and mucositis.', 'DDInter', 1767317477, 'Concomitant use of glasdegib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. Otherwise, close monitoring for increased adverse effects is advisable, including more frequent ECG monitoring.', 'Metabolism', 'Concomitant use of glasdegib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509127, 'Glecaprevir', 'Afatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of afatinib, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised if afatinib is used in combination with P-gp inhibitors. Patients should be monitored for potentially increased adverse effects such as diarrhea, which may lead to dehydration with or without renal impairment; cutaneous reactions including rash, erythema, and bullous, blistering, or exfoliating lesions; interstitial lung disease such as lung infiltration, pneumonitis, acute respiratory distress syndrome, and allergic alveolitis; hepatotoxicity, which may be life-threatening or fatal; keratitis characterized by acute or worsening eye inflammation, lacrimation, light sensitivity, blurred vision, eye pain, red eye, and/or ulceration; and left ventricular dysfunction. The manufacturer recommends reducing the daily dose of afatinib by 10 mg if not tolerated. The previous dose may be resumed after discontinuation of the P-gp inhibitor as tolerated.', 'Others', 'Caution is advised if afatinib is used in combination with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509128, 'Glecaprevir', 'Aliskiren', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein may increase the plasma concentrations and pharmacologic effects of aliskiren, which is a substrate of both the isoenzyme and efflux transporter. According to the product labeling, plasma levels of aliskiren were increased approximately 80% by the potent CYP450 3A4 and moderate P-gp inhibitor ketoconazole at a dosage of 200 mg twice daily.', 'DDInter', 1767317477, 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of aliskiren such as dizziness, lightheadedness, diarrhea, abdominal pain, and gastroesophageal reflux.', 'Metabolism', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509129, 'Glecaprevir', 'Aminoglutethimide', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) or CYP450 3A4 may decrease the plasma concentrations of glecaprevir and pibrentasvir. Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
