INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509148, 'Glecaprevir', 'Cenobamate', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) or CYP450 3A4 may decrease the plasma concentrations of glecaprevir and pibrentasvir. Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509149, 'Glecaprevir', 'Cobicistat', 'Moderate', 'Coadministration with cobicistat may significantly increase the plasma concentrations of glecaprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by cobicistat. Inhibition of P-glycoprotein-mediated intestinal efflux and CYP450 3A4-mediated metabolism of glecaprevir may also contribute.', 'DDInter', 1767317477, 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with cobicistat. However, the potential for increased adverse effects of glecaprevir-pibrentasvir should be considered.', 'Metabolism', 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with cobicistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509150, 'Glecaprevir', 'Cobimetinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of cobimetinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities and rhabdomyolysis, and the cobimetinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509151, 'Glecaprevir', 'Colchicine', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 1767317477, 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509152, 'Glecaprevir', 'Cyclosporine', 'Major', 'Coadministration with cyclosporine may significantly increase the plasma concentrations of glecaprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by cyclosporine. Inhibition of P-glycoprotein (P-gp)- and breast cancer resistance protein (BCRP)-mediated intestinal transport and CYP450 3A4-mediated metabolism of glecaprevir may also contribute. High plasma levels of glecaprevir may increase the risk of adverse effects such as alanine aminotransferase (ALT) and bilirubin elevations.', 'DDInter', 1767317477, 'Glecaprevir-pibrentasvir is not recommended for use in patients requiring stable cyclosporine dosages greater than 100 mg/day.', 'Metabolism, Absorption', 'Glecaprevir-pibrentasvir is not recommended for use in patients requiring stable cyclosporine dosages greater than 100 mg/day.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509153, 'Glecaprevir', 'Darolutamide', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by darolutamide. Increased adverse reactions of the OATP 1B1 and 1B3 substrates may result.', 'DDInter', 1767317477, 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darolutamide is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when darolutamide is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509154, 'Glecaprevir', 'Darunavir', 'Major', 'Coadministration with boosted darunavir or lopinavir/ritonavir may significantly increase the plasma concentrations of glecaprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by darunavir, lopinavir, and ritonavir. Inhibition of intestinal and/or biliary efflux of glecaprevir via P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP) may also contribute.', 'DDInter', 1767317477, 'Concomitant use of glecaprevir with lopinavir-ritonavir or darunavir boosted with either ritonavir or cobicistat is not recommended.', 'Metabolism', 'Concomitant use of glecaprevir with lopinavir-ritonavir or darunavir boosted with either ritonavir or cobicistat is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509155, 'Glecaprevir', 'Daunorubicin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317477, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509156, 'Glecaprevir', 'Daunorubicin (liposomal)', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317477, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
