INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509159, 'Glecaprevir', 'Digoxin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the serum concentrations of digoxin. The proposed mechanism is inhibition of the P-glycoprotein (P-gp)-mediated intestinal efflux or renal tubular secretion of digoxin by glecaprevir and pibrentasvir, both of which are inhibitors of the transporter.', 'DDInter', 1767317477, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir. The manufacturer recommends measuring serum digoxin levels before initiating glecaprevir-pibrentasvir and reducing the levels by decreasing the digoxin dose approximately 50% or by modifying the dosing frequency, followed by continued monitoring. Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption, Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of glecaprevir-pibrentasvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509160, 'Glecaprevir', 'Edoxaban', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil. This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509161, 'Glecaprevir', 'Efavirenz', 'Major', 'Coadministration with potent inducers of P-glycoprotein (P-gp) or CYP450 3A4 may significantly decrease the plasma concentrations of glecaprevir and pibrentasvir. Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of glecaprevir and pibrentasvir with potent P-gp or CYP450 3A4 inducers is not recommended.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of glecaprevir and pibrentasvir with potent P-gp or CYP450 3A4 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
