INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509163, 'Glecaprevir', 'Enasidenib', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', 'DDInter', 1767317477, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Distribution', 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509164, 'Glecaprevir', 'Entrectinib', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the OATP 1B1 (organic anion transporting polypeptide 1B1) transporter. The proposed mechanism, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of OATP1B1 transport protein.', 'DDInter', 1767317477, 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised when entrectinib is prescribed with drugs that are OATP1B1 substrates, particularly those with a narrow therapeutic range such as atorvastatin, pravastatin, rosuvastatin, repaglinide, and bosentan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
