INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509167, 'Glecaprevir', 'Esomeprazole', 'Moderate', 'Coadministration with omeprazole may decrease the plasma concentrations of glecaprevir. The mechanism of interaction has not been established.', 'DDInter', 1767317477, 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with omeprazole. However, the potential for diminished pharmacologic effects of glecaprevir-pibrentasvir should be considered during coadministration with higher dosages of omeprazole. Alternative treatments may be required if an interaction is suspected.', 'Others', 'According to the manufacturer, no dosage adjustment is necessary for glecaprevir-pibrentasvir during coadministration with omeprazole.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509168, 'Glecaprevir', 'Ethinylestradiol', 'Major', 'Coadministration of ethinyl estradiol with glecaprevir-pibrentasvir may increase the risk of alanine aminotransferase (ALT) elevations. ALT elevations are also associated with glecaprevir, particularly at high exposures, thus an additive risk may occur. Pharmacokinetically, glecaprevir-pibrentasvir may increase the plasma concentrations of ethinyl estradiol, presumably due to mild inhibition of CYP450 3A4-mediated metabolism.', 'DDInter', 1767317477, 'Concomitant use of ethinyl estradiol-containing medications with glecaprevir-pibrentasvir is not recommended and is considered contraindicated by some authorities.', 'Metabolism, Synergism', 'Concomitant use of ethinyl estradiol-containing medications with glecaprevir-pibrentasvir is not recommended and is considered contraindicated by some authorities.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509169, 'Glecaprevir', 'Etravirine', 'Moderate', 'Coadministration with inducers of P-glycoprotein (P-gp) or CYP450 3A4 may decrease the plasma concentrations of glecaprevir and pibrentasvir. Both antiviral agents are substrates of the P-gp efflux transporter, and glecaprevir is additionally a substrate of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of glecaprevir and pibrentasvir should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1509170, 'Glecaprevir', 'Everolimus', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the blood concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Absorption', 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
